import React, { useState } from 'react'

export const Form = (props) => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const autor = 'Вася Пупкин'
    // const createdNewDate = () => Date.now().getUnixTime()

    const onChangeName = (e) => {
        setTitle(e.target.value)
    }

    const onChangeDescription = (e) => {
        setDescription(e.target.value)
    }

    const submit = async (e) => {
        e.preventDefault();
        const createdDate = new Date().toUTCString()
         await props.newPost({ title, description,autor,createdDate});
        setTitle('');
        setDescription('')
}

    return (
        <form onSubmit={submit}>
            <input type='text' placeholder='Title' value={title} onChange={onChangeName} />
            <input type='text' placeholder='Desription' value={description} onChange={onChangeDescription} />
            <button type='submit'>Submit</button>
        </form>
    )
}
