import React, { Component } from 'react'
import Link from 'next/link'
import { Form } from '../src/Components/form'
import { postsApi } from '../src/api/api';
import Router from 'next/router'

class Post extends Component {

  state = {
    posts: this.props.posts || []
  };

  static getInitialProps = async (context) => {
    if (context.req) {
      return { posts: context.query }
    } else {
      return { posts: await postsApi.getAllPosts() }
    }
  }

  newPost = async (post) => {
    const response = await postsApi.createNewPost(post)
    this.setState({
      posts: [...this.state.posts, response]
    });
  }

  deletePost = async (id) => {
    const response = await postsApi.dellPost(id)
    this.setState({
      posts: this.state.posts.filter(post => post._id !== response.id)
    });
  }



  // updatePost = async (id) => {
  //   const response = await fetch(`http://localhost:3000//posts/update/${id}`, {
  //     method: 'PUT',
  //     headers: {
  //       'Content-Type': 'application/json;charset=utf-8'
  //     },
  //     body: JSON.stringify(this.state.newDescription)
  //   })
  //   const json = await response.json()
  //   console.log(json)

  // this.setState({
  //   posts: [...this.state.posts, json]
  // });
  // }
  handler = (url,id) => {
    Router.push({
      pathname: url,
      query: { id },
    })
  }



  render() {
    const posts = this.state.posts;
    return (
      <div>
        <Link href="/">
          <a>главная</a>
        </Link>
        {posts.map(post =>
          <div key={post._id} onClick={() => this.handler(`/view/`,post._id)}>
            <h1>{post.title}</h1>
            <p>Автор: {post.autor}</p>
            <p>createdDate: {post.createdDate.toString()}</p>
            <button onClick={() => this.deletePost(post._id)}>delete</button>
          </div>
        )}
        <Form newPost={this.newPost} />
      </div>
    )
  }
}
export default Post;
