import React, { useState, useEffect } from 'react'
import Link from 'next/link'
import Router, { withRouter } from 'next/router'
import { postsApi } from '../src/api/api';
import { Form } from '../src/Components/form';

const View = (props) => {
    const [post, setPost] = useState(props.post)
    const [showForm, setToogleSowForm] = useState(false)
    const isAutor = true

    const toogleShowForm = () => {
        setToogleSowForm(!showForm)
    }


    const deletePost = async (id) => {
        await postsApi.dellPost(id)
        setPost('Пост был удален')
    }

    const updatePost = async (data) => {
        const response = await fetch(`http://localhost:3000/posts/update/${data.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(data.post)
        })
        const json = await response.json()
        setPost(json)
    }

    return (
        <div>
            <Link href="/">
                <a>Главная</a>
            </Link>
            {
                (typeof post === 'object')
                    ?
                    <div>
                        <p>{post.title}</p>
                        <p>{post.autor}</p>
                        <p>{post.description}</p>
                    </div>
                    : <p>{post}</p>
            }
            {
                isAutor && <div>
                    <button disabled={typeof post !== 'object'} onClick={() => deletePost(post._id)}>Удалить</button>
                    <button onClick={toogleShowForm}>{!showForm ? 'Изменить' : 'Отменить'}</button>
                </div>
            }
            {
                showForm && <Form newPost={updatePost} id={post._id} autor='Вася Пупкин' />
            }

            <Link href='/posts'>
                <a>Посты</a>
            </Link>
        </div>

    )
}
View.getInitialProps = async (context) => {
    return { post: context.query.post }
}
export default withRouter(View);
