import Link from 'next/link'
import Router, { withRouter } from 'next/router'

const View = (props) => {
    const postId = props.router.query.id
    console.log(props)

    return (
        <div>
            <Link href="/">
                <a>back</a>
            </Link>
            <div>view&edit</div>
            <span onClick={() => Router.push('/posts')}>posts</span>
        </div>

    )
}
View.getInitialProps = async (context) => {
    if (context.req) {
      return { post: context.query }
    }

  const res = await fetch(`http://localhost:3000/view/${postId}`)
  const json = await res.json()
  return { post: json.post }
}
export default withRouter(View);
